-- ----------------------------
-- Procedure structure for usp_store_baocao_bangkenhapkho_theonguonvon
-- ----------------------------
DROP PROCEDURE IF EXISTS `usp_store_baocao_bangkenhapkho_theonguonvon`;
delimiter ;;
CREATE PROCEDURE `usp_store_baocao_bangkenhapkho_theonguonvon`(IN `p_ngay_batdau` DATETIME,
	IN `p_ngay_ketthuc` DATETIME,
	IN `p_nguoncungcap_id` INT)
BEGIN
	SELECT ncc.ma_nguoncungcap, ncc.ten_nguoncungcap
		, sp.ma_sanpham, sp.ten_sanpham, sp.ten_hoatchat, sp.nongdo_hamluong
		, dvt.ten_donvitinh
		, pn.ngay_laphoadon
		, k.ten_kho
		, pnct.so_chungtu, pnct.so_lo, pnct.hansudung, pnct.dongianhap, pnct.soluongnhap

	FROM store_phieunhap_chitiet pnct
		join store_phieunhap pn ON pnct.phieunhap_id = pn.id
		join store_nguoncungcap ncc ON pnct.nguoncungcap_id = ncc.id
		join store_sanpham sp ON pnct.sanpham_id = sp.id
		join store_donvitinh dvt ON sp.donvitinh_id = dvt.id
		join store_kho k ON pnct.nhap_vao_kho_id = k.id
	WHERE pnct.nguoncungcap_id = p_nguoncungcap_id
		AND pn.ngay_nhapkho BETWEEN p_ngay_batdau AND p_ngay_ketthuc;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for usp_store_baocao_bangkenhapkho_tonghop
-- ----------------------------
DROP PROCEDURE IF EXISTS `usp_store_baocao_bangkenhapkho_tonghop`;
delimiter ;;
CREATE PROCEDURE `usp_store_baocao_bangkenhapkho_tonghop`(IN `p_ngay_batdau` DATETIME,
	IN `p_ngay_ketthuc` DATETIME,
	IN `p_nguoncungcap_id` INT,
	IN `p_sanpham_id` INT,
	IN `p_sanpham_nhom_id` INT,
	IN `p_sanpham_loai_id` INT)
BEGIN
	SELECT ncc.ma_nguoncungcap, ncc.ten_nguoncungcap
		, sp.ma_sanpham, sp.ten_sanpham, sp.ten_hoatchat, sp.nongdo_hamluong
		, dvt.ten_donvitinh
		, pn.ngay_laphoadon
		, k.ten_kho
		, pnct.so_chungtu, pnct.ngay_chungtu, pnct.so_lo, pnct.hansudung, pnct.dongianhap, pnct.soluongnhap
	FROM store_phieunhap_chitiet pnct
		JOIN store_phieunhap pn ON pnct.phieunhap_id = pn.id
		LEFT JOIN store_nguoncungcap ncc ON pnct.nguoncungcap_id = ncc.id
		JOIN store_sanpham sp ON pnct.sanpham_id = sp.id
		JOIN store_donvitinh dvt ON sp.donvitinh_id = dvt.id
		JOIN store_kho k ON pnct.nhap_vao_kho_id = k.id
		LEFT JOIN store_sanpham_nhom_loai_rel nlrel ON pnct.sanpham_id = nlrel.sanpham_id
	WHERE pn.nhapxuat_id != 45
		AND pn.ngay_nhapkho BETWEEN p_ngay_batdau AND p_ngay_ketthuc
		AND (p_nguoncungcap_id = 0 OR pnct.nguoncungcap_id = p_nguoncungcap_id)
        AND (p_sanpham_id = 0 OR pnct.sanpham_id = p_sanpham_id)
				AND (p_sanpham_nhom_id = 0 OR nlrel.sanpham_nhom_id = p_sanpham_nhom_id)
				AND (p_sanpham_loai_id = 0 OR nlrel.sanpham_loai_id = p_sanpham_loai_id)
				;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for usp_store_baocao_bangkexuatkho_theosanpham
-- ----------------------------
DROP PROCEDURE IF EXISTS `usp_store_baocao_bangkexuatkho_theosanpham`;
delimiter ;;
CREATE PROCEDURE `usp_store_baocao_bangkexuatkho_theosanpham`(IN `p_ngay_batdau` DATETIME,
	IN `p_ngay_ketthuc` DATETIME,
	IN `p_sanpham_id` INT)
BEGIN
	SELECT ncc.ma_nguoncungcap, ncc.ten_nguoncungcap
		, sp.ma_sanpham, sp.ten_sanpham, sp.ten_hoatchat, sp.nongdo_hamluong
		, dvt.ten_donvitinh
		, pn.ngay_laphoadon
		, k.ten_kho
		, pnct.so_chungtu, pnct.so_lo, pnct.hansudung, pnct.dongianhap, pnct.soluongnhap

	FROM store_phieuxuat_chitiet pxct
		join store_phieuxuat px on pxct.phieuxuat_id = px.id
		join store_phieunhap_chitiet pnct on pxct.phieunhap_chitiet_id = pnct.id
		join store_phieunhap pn ON pnct.phieunhap_id = pn.id
		left join store_nguoncungcap ncc ON pnct.nguoncungcap_id = ncc.id
		join store_sanpham sp ON pxct.sanpham_id = sp.id
		join store_donvitinh dvt ON sp.donvitinh_id = dvt.id
		join store_kho k ON pxct.xuat_tu_kho_id = k.id
	WHERE pxct.sanpham_id = p_sanpham_id
		 AND px.ngay_xuatkho BETWEEN p_ngay_batdau AND p_ngay_ketthuc;
END
;;
