-- ----------------------------
-- Procedure structure for usp_store_danhsach_sanpham
-- ----------------------------
DROP PROCEDURE IF EXISTS `usp_store_danhsach_sanpham`;
delimiter ;;
CREATE PROCEDURE `usp_store_danhsach_sanpham`(IN `p_kho_id` INT,
	IN `p_ngay_xuatkho` DATETIME)
BEGIN
	/* --- Danh sách Sản phẩm còn số lượng --- */
	SELECT pnct.id
		, sp.ma_sanpham, sp.ten_sanpham, sp.ten_hoatchat, sp.sokiemsoat
		, dvt.ten_donvitinh
		, pnct.hansudung, pnct.soluong_conlai, pnct.dongianhap, pnct.so_lo, pnct.nongdohamluong
		, ncc.ma_nguoncungcap as nguoncungcap
	FROM store_phieunhap_chitiet pnct
		JOIN store_phieunhap pn on pnct.phieunhap_id = pn.id
		join store_sanpham sp on pnct.sanpham_id = sp.id
		join store_donvitinh dvt on sp.donvitinh_id = dvt.id
		left join store_nguoncungcap ncc on pnct.nguoncungcap_id = ncc.id
	WHERE pnct.nhap_vao_kho_id = p_kho_id
		 -- AND pnct.soluong_conlai > 0
		 -- AND IFNULL(pnct.hansudung, '3000-01-01') >= p_ngay_xuatkho
		;
END
;;
delimiter ;

