INSERT INTO `wp_posts` VALUES (239, 1, '2018-03-29 15:37:02', '2018-03-29 15:37:02', '<div>Vi khuẩn gây bệnh lậu có xu hướng nhân đôi, phát triển và biến chứng cực kì nhanh chóng vì thế mọi người tuyệt đối không thể coi thường những tác hại của bệnh lậu ở nam giới.</div>', 'Tác hại của bệnh lậu ở nam giới không thể coi thường', '', 'publish', 'open', 'open', '', 'tac-hai-cua-benh-lau-o-nam-gioi-khong-the-coi-thuong', '', '', '2018-03-29 15:37:02', '2018-03-29 15:37:02', '', 0, 'http://news.aigiupminhkhong.vn/?p=239', 0, 'post', '', 0);
